mapper.select:
SELECT W.ROW, W.ITEM_ID, W.BOARD_ID, W.TITLE, W.HIT_COUNT, W.CONTENTS, DATE_FORMAT(W.START_DATE,"%Y-%m-%e") AS START_DATE, DATE_FORMAT(W.END_DATE, "%Y-%m-%e") AS END_DATE, W.REGISTER_ID, W.REGISTER_NAME, DATE_FORMAT(W.REGISTER_DATE, "%Y-%m-%e") AS REGISTER_DATE FROM ( SELECT @ROW := @ROW + 1 AS ROW, O.* FROM ( SELECT ITEM_ID, BOARD_ID, TITLE, HIT_COUNT, CONTENTS, START_DATE, END_DATE, REGISTER_ID, REGISTER_NAME, REGISTER_DATE FROM WEEKEND_BD_BOARD_ITEM
WHERE BOARD_ID = #{boardId}
WHERE BOARD_ID in ( #{entity} )
AND TITLE like '%${searchWord}%'
ORDER BY REGISTER_DATE DESC ) O, ( SELECT @ROW := 0 ) R ) W WHERE W.ROW >= #{startPageIndex} AND W.ROW <= #{endPageIndex}


SELECT  COUNT(*)
FROM 	WEEKEND_BD_BOARD_ITEM
WHERE 	BOARD_ID = #{boardId}

mapper.insert:
INSERT INTO WEEKEND_BD_BOARD_ITEM
(ITEM_ID, BOARD_ID, TITLE, HIT_COUNT, CONTENTS, START_DATE, END_DATE, REGISTER_ID, REGISTER_NAME, REGISTER_DATE) 
VALUES (#{itemId},#{boardId},#{title},1,#{contents},#{startDate},#{endDate},#{registerId},#{registerName},NOW());

mapper.delete:
DELETE 
FROM	WEEKEND_BD_BOARD_ITEM
WHERE	ITEM_ID = #{itemId}
AND		BOARD_ID = #{boardId}

mapper.select:
SELECT ITEM_ID, BOARD_ID, TITLE, HIT_COUNT, CONTENTS, START_DATE, END_DATE, REGISTER_ID, REGISTER_NAME, DATE(REGISTER_DATE) AS REGISTER_DATE, DATE FROM ( SELECT ITEM_ID, BOARD_ID, TITLE, HIT_COUNT, CONTENTS, START_DATE, END_DATE, REGISTER_ID, REGISTER_NAME, TIMESTAMP(REGISTER_DATE) AS REGISTER_DATE, DATE FROM WEEKEND_BD_BOARD_ITEM WHERE 1=1
AND BOARD_ID = #{board_id}
AND (BOARD_ID = #{board_id} or BOARD_ID = #{board_id1})
AND (TITLE LIKE '%${search_word}%' or CONTENTS LIKE '%${search_word}%')
ORDER BY REGISTER_DATE DESC ) T1 LIMIT #{start_index},#{end_index}

mapper.update:
UPDATE	WEEKEND_BD_BOARD_ITEM
SET		TITLE = #{title},
		CONTENTS = #{contents},
		START_DATE = #{startDate},
		END_DATE = #{endDate}
WHERE	ITEM_ID = #{itemId}
