mapper.delete:
DELETE 
FROM	WEEKEND_BD_BOARD_ITEM
WHERE	ITEM_ID = #{item_id}
AND		BOARD_ID = #{board_id}

mapper.select:
SELECT ITEM_ID, BOARD_ID, TITLE, HIT_COUNT, CONTENTS, START_DATE, END_DATE, REGISTER_ID, REGISTER_NAME, DATE(REGISTER_DATE) AS REGISTER_DATE, DATE FROM ( SELECT ITEM_ID, BOARD_ID, TITLE, HIT_COUNT, CONTENTS, START_DATE, END_DATE, REGISTER_ID, REGISTER_NAME, TIMESTAMP(REGISTER_DATE) AS REGISTER_DATE, DATE FROM WEEKEND_BD_BOARD_ITEM WHERE 1=1
AND BOARD_ID = #{board_id}
AND (BOARD_ID = #{board_id} or BOARD_ID = #{board_id1})
AND (TITLE LIKE '%${search_word}%' or CONTENTS LIKE '%${search_word}%')
ORDER BY REGISTER_DATE DESC ) T1 LIMIT #{start_index},#{end_index}
